const {
  HOOKS: { HAPI_PLUGINS },
} = require("../../../../src/addon/index");

exports[HAPI_PLUGINS] = function hapiPlugins() {
  return [
    {
      name: "foo-header",
      version: "0.1.0",
      register: async function (server) {
        server.ext("onPreResponse", async (req, h) => {
          req.response.header("foo", "bar");
          return h.continue;
        });
      },
    },
    {
      name: "baz-header",
      version: "0.1.0",
      register: async function (server) {
        server.ext("onPreResponse", async (req, h) => {
          req.response.header("baz", "42");
          return h.continue;
        });
      },
    },
  ];
};
